def create_params(data, channelCode):
    keywords = ['教育', '大学', '中学', '小学', '学院', '学校', '幼儿', '学生']
    
    all_params = []
    for keyword in keywords:
        for key in data.keys():    
            params = dict()
            params['stockTypes'] = ''
            params['channelCode'] = str(channelCode)
            params['sitewebName'] = key
            params['sitewebId'] = all_sitewebId.get(key)
            params['title'] = keyword
            params['stockNum'] = ''
            params['purchaserOrgName'] = ''
            params['performOrgName'] = ''
            params['stockIndexName'] = ''
            params['operateDateFrom'] = '2015-01-01'
            params['operateDateTo'] = ''
            
            all_params.append(params)

    return all_params


def create_provinces(channelCode):
    return create_params(provinces, channelCode.value)


